(70, 1, '2020-06-19 13:43:21', '2020-06-19 13:43:21', '<!-- wp:cover {\"url\":\"http://localhost/bk-foodcourt/wp-content/uploads/2020/06/other-small.jpg\",\"id\":58,\"dimRatio\":0,\"focalPoint\":{\"x\":\"0.55\",\"y\":\"0.31\"}} -->\n<div class=\"wp-block-cover\" style=\"background-image:url(http://localhost/bk-foodcourt/wp-content/uploads/2020/06/other-small.jpg);background-position:55% 31%\"><div class=\"wp-block-cover__inner-container\"><!-- wp:paragraph {\"align\":\"center\",\"placeholder\":\"Write title…\",\"textColor\":\"white\",\"fontSize\":\"large\"} -->\n<p class=\"has-text-color has-text-align-center has-large-font-size has-white-color\">Căn tin Bách Khoa Cơ sở 2</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"textColor\":\"white\"} -->\n<p class=\"has-text-color has-text-align-center has-white-color\"><p class=\"has-text-color has-text-align-center\">Write a short welcome message here</p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {\"align\":\"center\"} -->\n<div class=\"wp-block-button aligncenter\"><a class=\"wp-block-button__link\" href=\"http://localhost/bk-foodcourt/shop/\">Go shopping</a></div>\n<!-- /wp:button --></div></div>\n<!-- /wp:cover -->\n\n<!-- wp:heading {\"align\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Shop by Category</h2>\n<!-- /wp:heading -->\n\n<!-- wp:shortcode -->\n[product_categories limit=\"3\" columns=\"3\" orderby=\"menu_order\"]\n<!-- /wp:shortcode -->\n\n<!-- wp:heading {\"align\":\"center\"} -->\n<h2 class=\"has-text-align-center\">New In</h2>\n<!-- /wp:heading -->\n\n<!-- wp:woocommerce/product-new {\"columns\":4} /-->\n\n<!-- wp:heading {\"align\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Fan Favorites</h2>\n<!-- /wp:heading -->\n\n<!-- wp:woocommerce/product-top-rated {\"columns\":4} /-->\n\n<!-- wp:heading {\"align\":\"center\"} -->\n<h2 class=\"has-text-align-center\">On Sale</h2>\n<!-- /wp:heading -->\n\n<!-- wp:woocommerce/product-on-sale {\"columns\":4} /-->\n\n<!-- wp:heading {\"align\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Best Sellers</h2>\n<!-- /wp:heading -->\n\n<!-- wp:woocommerce/product-best-sellers {\"columns\":4} /-->', 'Trang chủ', '', 'inherit', 'closed', 'closed', '', '57-revision-v1', '', '', '2020-06-19 13:43:21', '2020-06-19 13:43:21', '', 57, 'http://localhost/bk-foodcourt/2020/06/19/57-revision-v1/', 0, 'revision', '', 0),
